mapper.select:
SELECT DISTINCT tb_article.*,tb_article_detail.article_name, tb_article_detail.article_description, tb_article_detail.customs, tb_article_detail.language_id, tb_article_seo.meta_title, tb_article_seo.meta_description FROM tb_article LEFT JOIN tb_article_category_relation ON tb_article_category_relation.article_id = tb_article.id LEFT JOIN tb_article_seo ON tb_article_seo.article_id = tb_article.id LEFT JOIN tb_article_detail ON tb_article_detail.article_id = tb_article.id WHERE 1 = 1
AND tb_article.id = #{articleId}
AND tb_article_category_relation.category_id = #{categoryId}
AND tb_article_detail.article_name LIKE CONCAT('%',#{articleName}, '%')
AND tb_article.status = #{status}
AND tb_article_category_relation.is_delete = 0
AND tb_article.is_delete = 0
AND tb_article.is_delete = #{isDelete}
AND tb_article_category_relation.is_delete = 0 ORDER BY tb_article.id desc

SELECT tb_article.* FROM tb_article WHERE id IN
#{id}
AND is_delete = 0 FOR UPDATE
